import * as React from "react";
import { MDXRemoteSerializeResult } from "next-mdx-remote";

import { Page, MainLayout } from "~/components/layout";
import { Icon, Space, Button, Block, Text, InlineText, Image, Link } from "~/components/primitive";
import { MDXContent, RateContent } from "~/components/shared";
import { withSafeRendering } from "~/hocs";
import { useDidMount } from "~/hooks";
import { useTranslation } from "~/features/i18n";
import twcss from "~/lib/twcss";
import { useStoreSelector, useStoreActionsDispatcher } from "~/stores";
import { selectWebsiteMetadata, T_WebsiteMetadata } from "~/stores/modules/metadata";
import { setLocales } from "~/stores/modules/page-config";
import { copyToClipboard } from "~/utils/browser";
import { getFormattedDatesDifference } from "~/utils/dates";
import { ROUTES } from "~/features/routing";
import type { T_ReactElement } from "~/types";

import { T_BlogPost } from "./service";

type T_PageProps = {
	post: T_BlogPost;
	postMDXContent: MDXRemoteSerializeResult;
};

function BlogPostPage({ post, postMDXContent }: T_PageProps): T_ReactElement {
	// hooks
	const { t } = useTranslation();
	const dispatch = useStoreActionsDispatcher();

	// effects
	useDidMount(() => {
		dispatch(setLocales(post.locales));
	});

	return (
		<Page
			config={{
				title: post.title,
				replaceTitle: true,
				description: post.description,
				pathname: `${ROUTES.BLOG}/${post.slug}`,
				image: post.thumbnail,
				disableSEO: Boolean(t("page:config:is_seo_enabled")),
			}}
		>
			<MainLayout title={post.title}>
				<BlogPostDetails
					publishedAt={post.publishedAt}
					updatedAt={post.updatedAt}
					isPublished={post.isPublished}
				/>
				<Space size={8} />

				<Image
					src={post.thumbnail}
					alt="Blog post thumbnail"
					className="tw-mx-auto tw-rounded-md"
				/>
				<Space size={8} />

				<MDXContent content={postMDXContent} />
				<Space size={8} />

				<RateContent />
				<Space size={8} />

				<BlogPostActions />
			</MainLayout>
		</Page>
	);
}

export default BlogPostPage;

